refsection.programlisting:
CREATE TABLE addresses_to_geocode(addid serial PRIMARY KEY, address text, lon numeric, lat numeric, new_address text, rating integer);
INSERT INTO addresses_to_geocode(address) VALUES ('529 Main Street, Boston MA, 02129'), ('77 Massachusetts Avenue, Cambridge, MA 02139'), ('28 Capen Street, Medford, MA'), ('124 Mount Auburn St, Cambridge, Massachusetts 02138'), ('950 Main Street, Worcester, MA 01610');
-- only update the first two addresses (850 ms) -- -- for large numbers of addresses you don't want to update all at once -- since the whole geocode must commit at once UPDATE addresses_to_geocode SET (rating, new_address, lon, lat) = (g.rating, pprint_addy(g.addy), ST_X(g.geomout)::numeric(8,5), ST_Y(g.geomout)::numeric(8,5) ) FROM (SELECT DISTINCT ON (addid) addid, (g1.geo).* FROM (SELECT addid, (geocode(address)) As geo FROM addresses_to_geocode As ag WHERE ag.rating IS NULL ) As g1 ORDER BY addid, rating LIMIT 2) As g WHERE g.addid = addresses_to_geocode.addid;
result ----- 2 rows affected, 850 ms execution time.
SELECT * FROM addresses_to_geocode WHERE rating is not null;
