refsection.programlisting:
-- Test entire table for coverage validity
SELECT true = ALL (
    SELECT ST_CoverageInvalidEdges(geom) OVER () IS NULL
    FROM coverage
    );

-- Populate demo table CREATE TABLE example AS SELECT * FROM (VALUES (1, 'POLYGON ((10 190, 30 160, 40 110, 100 70, 120 10, 10 10, 10 190))'::geometry), (2, 'POLYGON ((100 190, 10 190, 30 160, 40 110, 50 80, 74 110.5, 100 130, 140 120, 140 160, 100 190))'::geometry), (3, 'POLYGON ((140 190, 190 190, 190 80, 140 80, 140 190))'::geometry), (4, 'POLYGON ((180 40, 120 10, 100 70, 140 80, 190 80, 180 40))'::geometry) ) AS v(id, geom);
-- Prove it is a dirty coverage SELECT ST_AsText(ST_CoverageInvalidEdges(geom) OVER ()) FROM example;
-- Clean the coverage CREATE TABLE example_clean AS SELECT id, ST_CoverageClean(geom) OVER () AS GEOM FROM example;
-- Prove it is a clean coverage SELECT ST_AsText(ST_CoverageInvalidEdges(geom) OVER ()) FROM example_clean;
